export default {
  name: 'Register',

  data() {
    return {
      phone: '',
      code: '',
      password: '',
      password1: '',
      agree: false
    };
  },

  methods: {
    async getCode() {
      try {
        const {
          phone
        } = this;
        phone && (await this.$store.dispatch('getCode', phone));
        this.code = this.$store.state.user.code;
      } catch (error) {
        alert(error.message);
      }
    },

    async userRegister() {
      // 所有协议验证成功才能注册
      const success = await this.$validator.validateAll(); //全部表单验证成功

      if (success) {
        try {
          // 如果成功，路由跳转至登录页面
          const {
            phone,
            password,
            password1,
            code
          } = this;
          await this.$store.dispatch('userRegister', {
            phone,
            code,
            password
          });
          this.$router.push('/login');
        } catch (error) {
          alert(error.message);
        }
      }
    }

  }
};
