import { reqGetCode } from "@/api"

const state = {}

const mutations = {}

const actions = {
  // 获取验证码
  async getCode({ commit }, phone) {
    // 把验证码返回，正常情况应该是把验证码发到用户手机上
    let result = await reqGetCode(phone)
    // console.log(result);
    if (result.code == 200) {
      commit('GETCODE', result.data)
    }

  }
}

const getters = {}


export default {
  state, mutations, actions, getters
}
