import Vue from 'vue'
import Vuex from 'vuex'

// 使用vuex插件
Vue.use(Vuex);

// 存储数据的仓库
const state = {
  count: 1
}
// 修改state的唯一手段
const mutations = {
  ADD(state) {
    state.count++;
  },
  DES(state) {
    state.count--;
  }
}
// 处理actions,可以书写自己的业务逻辑,处理异步
const actions = {
  add({ commit }) {
    commit('ADD')
  },
  des({ commit }) {
    commit('DES')
  }
}

// 计算属性
const getters = {}

// 对外暴露Store类的一个实例
export default new Vuex.Store({
  state,
  actions,
  mutations,
  getters

})
