export default {
  name: 'Pagination',
  props: ['pageNo', 'pageSize', 'total', 'continues'],
  computed: {
    totalPage: function totalPage() {
      // 向上取整Math.ceil()
      return Math.ceil(this.total / this.pageSize);
    },
    startNumAndEndNum: function startNumAndEndNum() {
      var totalPage = this.totalPage,
          pageNo = this.pageNo,
          continues = this.continues;
      var start = 0,
          end = 0;

      if (continues > totalPage) {
        start = 1;
        end = totalPage;
      } else {
        // parseInt() 只取整数部分
        start = pageNo - parseInt(continues / 2);
        end = pageNo + parseInt(continues / 2); // start数字出现小于等于0的情况

        if (start < 1) {
          start = 1;
          end = continues;
        } // end大于总页数


        if (end > totalPage) {
          start = totalPage - continues + 1;
          end = totalPage;
        }
      }

      return {
        start: start,
        end: end
      };
    }
  },
  methods: {}
};
