import Swiper from 'swiper';
export default {
  name: 'ImageList',
  data: function data() {
    return {
      currentIndex: 0
    };
  },
  methods: {
    changeCurrentIndex: function changeCurrentIndex(index) {
      this.currentIndex = index;
      this.$bus.$emit('getIndex', this.currentIndex);
    }
  },
  props: ['skuImageList'],
  watch: {
    skuImageList: function skuImageList(newValue, oldValue) {
      var _this = this;

      this.$nextTick(function () {
        new Swiper(_this.$refs.cur, {
          // direction: 'vertical', // 垂直切换选项
          loop: true,
          // 循环模式选项
          // 如果需要分页器
          pagination: {
            el: '.swiper-pagination',
            clickable: true
          },
          // 如果需要前进后退按钮
          navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev'
          },
          // 同时显示多少张图片
          slidesPerView: 3,
          // 多少张图片为一组进行切换
          slidesPerGroup: 1,
          // 如果需要滚动条
          scrollbar: {
            el: '.swiper-scrollbar'
          }
        });
      });
    }
  }
};
