import Vue from 'vue'
import Vuex from 'vuex'

// 使用vuex插件
Vue.use(Vuex);

// 引入小仓库
import home from '@/store/home'
import search from '@/store/search';
import detail from '@/store/detail'

// 对外暴露Store类的一个实例
export default new Vuex.Store({
  modules: {
    home,
    search,
    detail
  }

})
