export default {
  name: 'Login',

  data() {
    return {
      phone: '',
      password: '',
      code: '',
      password1: '',
      agres: ''
    };
  },

  methods: {
    async userLogin() {
      try {
        const {
          phone,
          password
        } = this;
        phone && password && (await this.$store.dispatch('userLogin', {
          phone,
          password
        })); // 登录之前，判断路由中是否有query参数，即准备去的页面的路由，决定登录后跳转的页面

        let toPath = this.$route.query.redirect || '/home';
        this.$router.push(toPath);
      } catch (error) {
        alert(error.message);
      }
    }

  }
};
