// home模块的小仓库
import { reqCatagoryList } from "@/api"
const state = {
  categoryList: []
}

const mutations = {
  CATEGORYLIST(state) {
    state.categoryList = categoryList
  }
}

const actions = {
  async categoryList({ commit }) {
    let result = await reqCatagoryList();
    if (result.code == 200) {

      commit('CATEGORYLIST')
    }
  }
}

const getters = {}


export default {
  state, mutations, actions, getters
}
